function passValidate() {
    var password = document.getElementById("user_new_password").value;
    var confirmedPassword = document.getElementById("user_confirm_password").value;
    if (password != confirmedPassword)
    {
        alert("Password not match");
        return false;
    }
    return true;
}

function casePc() {

    var block = document.getElementById('myCaseBlock');
    var btn = document.getElementById('caseBtn');

    btn.onclick = function () {
        block.style.display = "block";
    }

    window.onclick = function (event) {
        if (event.target == block) {
            block.style.display = "none";
        }
    }
}


