function selectDelivery() {
    var checkBox = document.getElementById('delivery');
    var price = document.getElementById('delivery_price');
    if (checkBox.checked == true){
        price.style.display = "block";
    }
    else {
        price.style.display = "none";
    }
    
}

$(document).ready(function () {
    $('#selectAllBoxes').click(function (event) {
        if (this.checked)
        {
            $('.checkBoxes').each(function () {
                this.checked = true;
            })
        }
        else
        {
            $('.checkBoxes').each(function () {
                this.checked = false;
            })
        }
    })
});

function empty() {
    var mobo;
    var caseModel;
    var cpu;
    var ram;
    var hdd;
    var ssd;
    var graphicCard;
    var powerSupply;

    caseModel = document.getElementById("case_model").value;
    if (caseModel == "")
    {
        alert("Please select PC Case");
        return false;
    }
    mobo = document.getElementById("moboModel").value;
    if (mobo == "") {
        alert("Please select Motherboard");
        return false;
    }
    cpu = document.getElementById("cpu_model").value;
    if (cpu == "") {
        alert("Please select CPU");
        return false;
    }
    ram = document.getElementById("ramModel").value;
    if (ram == "") {
        alert("Please select RAM");
        return false;
    }
    hdd = document.getElementById("hddModel").value;
    ssd = document.getElementById("ssdModel").value;
    if (hdd == "" && ssd == "") {
        alert("Please select hdd/ssd or both");
        return false;
    }
    graphicCard = document.getElementById("GraphicCardModel").value;
    if (graphicCard == ""){
        alert("Please select Graphic Card");
        return false;
    }
    powerSupply = document.getElementById("powerSupplyModel").value;
    if (powerSupply == ""){
        alert("Please select Power Supply")
        return false;
    }
}

